using Microsoft.EntityFrameworkCore;
using SelectPdf;

namespace Mokkiprojekti_ryhma10.Utils
{

    internal static class InvoicePDFCreator
    {


        private static string TableRowHelper(params string[] rowData)
        {
            var result = "<tr>";
            foreach (var row in rowData)
            {
                result += $"<td>{row}</td>\n";
            }
            return result + "</tr>";

        }


        public static async Task CreatePDF(Reservation reservation)
        {
            string HTMLString = @"<html>

<head>
    <title>None</title>
    <style>
        * {
            font-family: Tahoma, Geneva, Verdana, sans-serif;
            margin: 0;
            padding: 0;
            box-sizing: border-box;
        }

        body {
            width: 800px;
            margin: auto;
            padding-top: 40px;
            height: 900px;
            display: flex;
            flex-direction: column;
            justify-content: space-between;
        }

        .header {
            display: flex;
            justify-content: space-between;
            color: grey;
            margin-bottom: 25px;
        }

        .invoice-info {
            background-color: #835534;
            display: flex;
            justify-content: space-between;
            padding: 5px 15px;
            color: white;
            margin-bottom: 10px;
        }

        .customer-info {
            margin-top: 15px;
            margin-bottom: 15px;
        }

        table {
            font-family: arial, sans-serif;
            border-collapse: collapse;
            width: 100%;
        }

        th {
            background-color: #835534;
            color: white;
        }

        tr {
            height: 40px;
        }

        td,
        th {
            border: 1px solid #dddddd;
            text-align: left;
            padding: 8px;
        }

        td:not(:first-child),
        th:not(:first-child) {
            text-align: right;
        }

        .header img {
            width: 250px;
        }
    </style>
</head>

<body>
    <div class='top'>
        <div class='header'>
            <p>Village Newbies Oy. <br>
                Siilokatu 1 <br>
                90700 OULU <br>
                Puh. 040 1234568 <br>
            </p>
            <img src='data:image/png;base64,'>
        </div>";
            using var _context = new CottageContext();
            var reservationData = _context.Reservations.Where(r => r.ReservationId == reservation.ReservationId)
                .Include(r => r.ReservationServices)
                    .ThenInclude(rs => rs.Service)
                .Include(r => r.Customer)
                    .ThenInclude(c => c.Post)
                .Include(r => r.Cottage)
                .Include(r => r.Invoices)
                .Select(r => new
                {
                    ID = r.ReservationId,
                    InvoiceID = r.Invoices.FirstOrDefault().InvoiceId,
                    TotalCost = r.Invoices.FirstOrDefault().Sum,
                    VAT = r.Invoices.FirstOrDefault().Vat,
                    CottageName = r.Cottage.CottageName,
                    CottagePrice = r.Cottage.Price,
                    CustomerID = r.CustomerId,
                    CustomerName = r.Customer.Display,
                    CustomerAddress = r.Customer.Address,
                    CustomerPostInfo = r.Customer.Post.Display,
                    InvoiceCreationDate = DateTime.Now.ToShortDateString(), // Mikä päivämäärä tähän?
                    Services = r.ReservationServices
                }).FirstOrDefault();

            // Jaottelin HTML eri osiin, vaikea sanoa tekeekö se tästä vain vaikemmin ymmärrettävän, muuttaa jos tarvitsee.
            string HTMLInvoiceInfo = @"<div class='invoice-info'>
                                            <h3>INVOICE #{0}</h3>
                                            <h3>{1}</h3>
                                        </div>";

            HTMLString += string.Format(HTMLInvoiceInfo, reservationData.InvoiceID, reservationData.InvoiceCreationDate);

            string HTMLCustomerInfo = @"<p>Customer #{0}</p>
                                        <hr>
                                        <div class='customer-info'>
                                                <p>{1}<br>
                                                   {2}<br>
                                                   {3}
                                                </p>
                                        </div>";
            HTMLString += string.Format(HTMLCustomerInfo,
                reservationData.CustomerID,
                reservationData.CustomerName,
                reservationData.CustomerAddress,
                reservationData.CustomerPostInfo);

            string HTMLTableTop = $@"<table>
            <tr>
                <th> DESCRIPTION </th>
                <th> UNIT PRICE </th>
                <th> AMOUNT </th>
                <th> TOTAL </th>
             </tr>";

            HTMLString += HTMLTableTop;


            HTMLString += TableRowHelper(
                reservationData.CottageName,
                reservationData.CottagePrice.ToString(),
                "1",
                reservationData.CottagePrice.ToString());

            foreach (var service in reservationData.Services)
            {
                var s = service.Service;
                HTMLString += TableRowHelper(service.Display, s.Price.ToString(), service.Amount.ToString(),
                    (s.Price * service.Amount).ToString());
            }
            DateTime duedate = DateTime.Now;
            duedate = duedate.AddDays(14);
            Random rnd = new Random();
            int referenceNumber = rnd.Next(1000, 1000000);
            // Alareunaa pitää varmastikin vielä hioa
            HTMLString += string.Format(@"
                    </table>
                   </div>
                   <div class='bottom'>
                    <p>Bank Account number: FI00 3235 3344 3300</p>
                    <p>Total: {0}</p>
                    <p>Due date: {1}</p>
                    <p>Reference number: {2}</p>
                    </div>
                    </body>
                    </html>", reservationData.TotalCost.ToString(), duedate.ToShortDateString(), referenceNumber.ToString());


            // Itse PDF:n luominen kesti useamman sekunnin, jolloin koko ohjelma jäätyi. 
            // Async - awaitin avulla jäätymistä ei tapahdu
            await Task.Run(() =>
            {
                HtmlToPdf converter = new HtmlToPdf();

                PdfDocument doc = converter.ConvertHtmlString(HTMLString);
                // TODO Minne tallennetaan?
                //   - Kysytäänkö käyttäjältä FileDialogilla tms.

                // Alustavasti laitoin tiedostonimeksi esim. "2022_03_14_11_20_11.pdf"
                string customername = reservationData.CustomerName;
                customername = customername.Replace(" ", "_");
                string fileName = DateTime.Now.ToString("yyyy_MM_dd_hh_mm_ss");
                doc.Save($@"C:\temp\{customername}{fileName}.pdf");

                doc.Close();
            });

        }
    }


}
